import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CodeWarsScores ({ nameCW }) {
  const nameCWMini = nameCW.toLowerCase()
  const [score, setScore] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://www.codewars.com/api/v1/users/Fenriz1349/${nameCWMini}`);
        // Le score sera la longueur du tableau de challenges complétés dans ce langage
        setScore(response.data.data.length);
      } catch (error) {
        console.error('Erreur lors de la récupération du score de CodeWars : ', error);
      }
    };

    fetchData();
  }, [nameCWMini]);

  return (
    <div>
      <p>Score en {nameCW} : {score}</p>
    </div>
  );
};

export default CodeWarsScores;
