// Importez useState et useEffect depuis 'react'
import { useState, useEffect } from 'react';
import logo from "../assets/logoJC.jpg";
import mail from "../assets/mailLogo.png";
import phone from "../assets/phoneLogo.png"
import github from "../assets/githubLogo.png";
import linkedin from "../assets/linkedinLogo.png";
import codewars from "../assets/codewarsLogo.png";
import menuLogo from "../assets/menuLogo.png"
import "../styles/Banner.css";
import jsonData from '../data/data.json';
import PhoneModal from './PhoneModal';

function Banner() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isPhoneModalOpen, setIsPhoneModalOpen] = useState(false);
  const [phoneModalPosition, setPhoneModalPosition] = useState({ x: 0, y: 0 });

  function scrollToTop() {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  }

  function toggleMobileMenu() {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  }

  function togglePhoneModal(event) {
    setIsPhoneModalOpen(!isPhoneModalOpen);
  }

  useEffect(() => {
    function handleWindowClick(event) {
      // Si la modal est ouverte et que l'événement de clic ne provient pas du bouton de téléphone, fermez la modal
      if (isPhoneModalOpen && event.target.id !== 'phone-button') {
        setIsPhoneModalOpen(false);
      }
    }

    // Ajoutez un écouteur d'événements pour gérer les clics en dehors de la modal
    window.addEventListener('click', handleWindowClick);

    // Nettoyez l'écouteur d'événements lors du démontage du composant
    return () => window.removeEventListener('click', handleWindowClick);
  }, [isPhoneModalOpen]); // Déclenchez l'effet lorsque la valeur de isPhoneModalOpen change

  return (
    <div className="banner">
      <button onClick={scrollToTop} className="scroll-to-top-button">
        <img src={logo} alt="Logo principal" className="logoBanner"/>
      </button>
      <button onClick={toggleMobileMenu} className="mobile-menu-toggle">
        <img src={menuLogo} alt="Logo menu" className="logoMenu"/>
        <div className={`hamburger-icon ${isMobileMenuOpen ? 'open' : ''}`}></div>
      </button>
      <nav className={`nav-links${isMobileMenuOpen ? 'open' : ''}`}>
        {/* Mettez vos éléments de navigation ici */}
      </nav>
      <div className={`social-banner${isMobileMenuOpen ? 'open' : ''}`}>
        <div id="phone-button" className="phone-button" onClick={togglePhoneModal}>
          <img src={phone} alt="phone" className="logo" />
        </div>
        {/* Autres liens sociaux */}
      </div>

      {/* Modal pour afficher le numéro de téléphone */}
      {isPhoneModalOpen && (
        <PhoneModal
          telephone={jsonData.informations.telephone}
          position={{ left: phoneModalPosition.x, top: phoneModalPosition.y }}
        />
      )}
    </div>
  );
}

export default Banner;
