import "../styles/MailModal.css"; 
import { useState } from 'react';

function MailModal({ mail, toggleMailModal }) {
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubjectChange = (event) => {
    setSubject(event.target.value);
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Vous pouvez ajouter le code pour envoyer l'e-mail ici
    console.log('E-mail envoyé à :', mail);
    console.log('Sujet :', subject);
    console.log('Message :', message);
    // Fermer la modal
    toggleMailModal();
  };

  return (
    <div className="mail-modal">
      <div className="mail-modal-content">
        <h4>Envoyer un e-mail à : {mail}</h4>
        <form onSubmit={handleSubmit}>
          <label>
            Sujet :
            <input type="text" value={subject} onChange={handleSubjectChange} />
          </label>
          <label>
            Message :
            <textarea value={message} onChange={handleMessageChange} />
          </label>
          <button type="submit">Envoyer</button>
          <button onClick={toggleMailModal}>Fermer</button>
        </form>
      </div>
    </div>
  );
}
    
export default MailModal;
